mapper.select:
SELECT bno 
FROM sv_board 
WHERE sv_type = #{sv_type}

SELECT bno 
FROM sv_board 
WHERE sv_type = #{sv_type}
AND group_origin = #{group_origin}

mapper.insert:
INSERT INTO sv_board 
(bno
, sv_type
, group_origin
, group_order
, admin_type
, nickname
, title
, description
, create_time
, heart
, view_no
, member_no
)
VALUES
(sv_bno_SEQUENCE.NEXTVAL
, #{sv_type}
, 1
, 1
, #{admin_type}
, #{nickname}
, #{title}
, #{description}
, #{create_time}
, 0
, 0
, #{member_no}
)
