mapper.select:
SELECT bno 
FROM sv_board 
WHERE sv_type = #{sv_type}

SELECT bno 
FROM sv_board 
WHERE sv_type = #{sv_type}
AND group_origin = #{group_origin}

mapper.insert:
INSERT INTO sv_board 
(bno
, sv_type
, group_origin
, group_order
, admin_type
, nickname
, title
, description
, create_time
, heart
, view_no
, member_no
)
VALUES
(sv_bno_SEQUENCE.NEXTVAL
, #{sv_type}
, 1
, 1
, #{admin_type}
, #{nickname}
, #{title}
, #{description}
, #{create_time}
, 0
, 0
, #{member_no}
)

mapper.update:
UPDATE sv_board
SET title = #{title},
description = #{description}
WHERE bno = #{bno}

UPDATE sv_board
SET view_no = #{view_no}
WHERE bno = #{bno}

mapper.select:
SELECT count(*) 
FROM sv_viewcheck 
WHERE bno = #{bno}
AND member_no = #{member_no}

SELECT count(*) 
FROM sv_viewcheck 
WHERE bno = #{bno}
AND member_ip = #{member_ip}

mapper.insert:
INSERT INTO sv_viewcheck 
(bno
, view_time
, member_ip
, member_no
)
VALUES
(#{bno}
, to_char(sysdate, 'yyyymmdd')
, 'none'
, #{member_no}
)

INSERT INTO sv_viewcheck 
(bno
, view_time
, member_ip
, member_no
)
VALUES
(#{bno}
, to_char(sysdate, 'yyyymmdd')
, #{member_ip}
, 0
)

mapper.select:
SELECT * 
FROM sv_viewcheck  
WHERE bno = #{bno}
AND member_no = #{member_no}

SELECT * 
FROM sv_viewcheck  
WHERE bno = #{bno}
AND member_ip = #{member_ip}
